package Narrowing;

public class Double {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//double to float
		double pooja=903663172574l;
		float aarti=(float)pooja;
		System.out.println(aarti);
		//double to long
		double a=89999393939l;
		long b=(long)a;
		System.out.println(b);
		//double to int
				double c=92.778;
				int d=(int)c;
				System.out.println(d);
				//double to short
				double e=90.99;
				short f=(short)e;
				System.out.println(f);
				//double to byte
				double g=9.9;
				short h=(short)g;
				System.out.println(h);
				
				

	}

}
