package Narrowing;

public class Float {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//float to int
		float a =10.10f;
		int b =(int)a;
		System.out.println(b);
		//float to long
		float u=1000.100f;
		long h=(long)u;
		System.out.println(h);
		//float to short
		float  w = 13.445f;
		short k=(short)w;
		System.out.println(k);
		float y=12.12f;
		byte l = (byte)y;
		System.out.println(l);
	
		
	}
	}
