package Narrowing;

public class Long {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//long to int
				long adhar=9036999999l;
				int j =(int)adhar;
				System.out.println(j);
				//long to short
				long a=900l;
				short l =(short)a;
				System.out.println(l);
				//long to byte
				long b =135l;
				int c =(int)b;
				System.out.println(c);
	}

}
