package casting;

public class Byte {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte to short
		byte a=30;
		short b=a;
		System.out.println(b);
		//byte to int
				byte c=16;
				int d=c;
				System.out.println(d);
				//byte to long
				byte e=89;
				long f=e;
				System.out.println(f);
				//byte to float
				byte g=56;
				float h=g;
				System.out.println(h);
				//byte to double
				byte i=98;
				double k=i;
				System.out.println(k);
	}

}
