package casting;

public class Double {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int to double

		int a=50;
		double b=a;
		System.out.println(b);
		//char to int
		char j='A';
		int h=j;
		System.out.println(h);
	//ASCII Values A=65 B=66 C=67 a=97 b=98
	}

}
