package casting;

public class Integer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int to long
				int a=67;
				long b=a;
				System.out.println(b);
				//int to float
				int c=67;
				float d=c;
				System.out.println(d);
				//int to double
				int h=679;
				long y=h;
				System.out.println(y);

	}

}
