package casting;

public class Short {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//short to int
		short a=67;
		int b=a;
		System.out.println(b);
		//short to long
				short c=67;
				long d=c;
				System.out.println(d);
				//short to float
				short e=67;
				float f=e;
				System.out.println(f);
				//short to double
				short j=67;
				double h=j;
				System.out.println(h);
	}

}
